/* Force Google sticky ad to have lower z-index */
._ap_apex_ad,
[id*="STICKY_ADP_"],
[id*="google_ads_iframe"] {
    z-index: 1 !important;
}

/* Ensure menu stays on top */
.simple-menu-overlay,
.simple-menu-content {
    z-index: 2147483647 !important;
}

.simple-menu-overlay {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.5);
    display: none;
    overflow: hidden;
}

.simple-menu-content {
    position: fixed;
    top: 10px;
    right: calc((100vw - min(1440px, 100vw)) / 2 + 16px);
    width: calc(100% - 32px);
    max-width: 530px;
    height: auto;
    max-height: 75vh;
    background: #FFF;
    border-radius: 12px;
    padding: 13px 20px 100px 20px;
    overflow: hidden;
    display: flex;
    flex-direction: column;
}

.simple-menu-content.rtl {
    direction: rtl;
    text-align: right;
}

.simple-close-btn {
    display: flex;
    align-items: flex-end;
    justify-content: flex-end;
    margin-bottom: 20px;
    background: none;
    border: none;
    cursor: pointer;
    margin-right: -11px;
    padding: 0;
}

.menu-content {
    flex: 1;
    overflow-y: auto;
    overflow-x: hidden;
    padding-bottom: 60px;
}

.simple-menu-links {
    display: flex;
    gap: 4px;
}

.menu-column {
    flex: 1;
    display: flex;
    flex-direction: column;
    padding-right: 16px;
}

.section-title {
    color: #989898;
    font-family: "Red Hat Display";
    font-size: 16px;
    font-weight: 700;
    line-height: 35px;
    text-transform: uppercase;
    margin-bottom: 12px;
}

.section-items {
    display: flex;
    flex-direction: column;
    gap: 8px;
}

.section-items a {
    color: #000;
    font-family: "Red Hat Display";
    font-size: 16px;
    font-weight: 700;
    line-height: 30px;

    text-decoration: none;
    transition: color 0.2s ease;
    white-space: normal; /* Allow text wrapping */
    word-wrap: break-word;
}

.section-items a:hover,
.section-items a.active {
    color: #2CBF92;
}

.section-divider {
    height: 1px;
    background: #E5E5E5;
    margin: 16px 0;
}

.vertical-divider {
    width: 1px;
    background: #E5E5E5;
    margin: 0 20px;
}



.simple-menu-buttons {
    position: absolute;
    bottom: 0;
    left: 0;
 width: 100%;
    display: flex;
    gap: 12px;
    padding: 20px;
    background: #FFF;
    border-bottom-left-radius: 12px;
    border-bottom-right-radius: 12px;
    box-sizing: border-box;
}

.simple-download-btn {
    flex: 1;
    background: #000;
    color: white;
    border: none;
    border-radius: 8px;
    font-family: "Red Hat Display";
    font-size: 14px;
    font-weight: 600;
    height: 50px;
    cursor: pointer;
}

.simple-login-btn {
    flex: 1;
    background: transparent;
    color: #000;
    border: 2px solid #1F1F1F;
    border-radius: 10px;
    font-family: "Red Hat Display";
    font-size: 14px;
    font-weight: 600;
    height: 50px;

    cursor: pointer;
}

/* Mobile responsive */
@media (max-width: 768px) {
    .simple-menu-content {
        width: calc(100% - 32px);
        right: 0;
        top: 0;
        height: 100%;
        border-radius: 0;
        padding: 10px 16px 120px 16px;
        overflow: hidden;
        max-width: 100%;
        max-height: 80vh;
    }

    .menu-content {
        overflow-y: auto;
        overflow-x: hidden;
    }

    .simple-close-btn {
        margin-right:-6px;
    }
    .simple-menu-links {
        flex-direction: row; /* Keep two columns like desktop */
        gap: 0px; /* Remove gap in mobile */
        /* margin-top: 40px; */
    }

    .vertical-divider {
        display: block; /* Show vertical divider like desktop */
        width: 1px;
        background: #E5E5E5;
        margin: 0 20px;
    }
    .section-title {
        font-size: 10px;
        line-height: 30px;
        margin-bottom: 8px;
        color: #989898;
        font-weight: 700;
        text-transform: uppercase;
    }

    .section-items a {
        font-size: 14px;
        line-height: 30px;
        color: #000;
        font-weight: 700;
        padding-right: 8px;
    }

    .section-items {
        gap: 6px;
    }



    .simple-menu-buttons {
        position: fixed;
        bottom: 0;
        left: 0;
        right: 0;
        flex-direction: column;
        gap: 8px;
        padding: 16px 16px 16px 16px;
        border-bottom-left-radius: 0;
        border-bottom-right-radius: 0;
        height: auto;
        /* min-height: 120px; */
        width: 100%;
        z-index: 10000;
        background: #FFF;
        box-sizing: border-box;
    }

    .simple-download-btn,
    .simple-login-btn {
        width: 100%;
        padding: 14px 24px;
    }
}

/* Custom scrollbar */
.menu-content::-webkit-scrollbar {
    width: 2px;
}

.menu-content::-webkit-scrollbar-track {
    background: transparent;
}

.menu-content::-webkit-scrollbar-thumb {
    background: #BDBDBD;
    border-radius: 1px;
    min-height: 30px;
}

.menu-content::-webkit-scrollbar-thumb:hover {
    background: #A0A0A0;
}

