}

body::-webkit-scrollbar-track {
    background: transparent;
}

body::-webkit-scrollbar-thumb {
    background: #BDBDBD;
    border-radius: 3px;
}

body::-webkit-scrollbar-thumb:hover {
    background: #A0A0A0;
}



function toggleSimpleMenu() {
    const overlay = document.getElementById('simpleMenuOverlay');
    if (overlay.style.display === 'block') {
        overlay.style.display = 'none';
        document.body.style.overflow = 'auto'; // Re-enable body scroll
        document.body.style.paddingRight = ''; // Remove padding compensation
    } else {
        overlay.style.display = 'block';
        document.body.style.overflow = 'hidden'; // Disable body scroll

        // Calculate scrollbar width and compensate to prevent layout shift
        const scrollbarWidth = window.innerWidth - document.documentElement.clientWidth;
        if (scrollbarWidth > 0) {
            document.body.style.paddingRight = scrollbarWidth + 'px';
        }
    }
}
